import React, { useEffect, useState } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer() {
  // ... existing code ...

  const addTransaction = (newTransaction) => {
    // Update the frontend state with the new transaction
    setTransactions([...transactions, newTransaction]);

    // Send a POST request to the backend API (replace 'url' with your actual API endpoint)
    fetch("url", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ transactions: [...transactions, newTransaction] }),
    })
      .then((response) => response.json())
      .then((data) => console.log("Transaction added successfully:", data))
      .catch((error) => console.error("Error adding transaction:", error));
  };

  return (
    <div>
      <Search />
      <AddTransactionForm onAddTransaction={addTransaction} />
      <TransactionsList transactions={transactions} />
    </div>
  );
}

export default AccountContainer;
